import { jsx } from 'theme-ui';
import { Container, Box, Heading, Text, Image, Button } from 'theme-ui';
import Excersise from 'assets/excersise.png';
import BannerImg from 'assets/banner-thumb.png';
import styles from './styles';
import ContactUs from 'sections/contact-us/index';
import IndexPage from 'pages/index';
import React, {useRef, useState} from 'react';


// const contactUsRef = useRef();

export default function Banner() {

  const ScrollButton = () =>{ 
    
    const [visible, setVisible] = useState(true) 
      
    const toggleVisible = () => { 
      const scrolled = document.documentElement.scrollTop; 
      if (scrolled > 0){ 
        setVisible(false) 
      }  
      else if (scrolled <= 0){ 
        setVisible(true) 
      } 
    };
    
  // const scrollToBottom = () =>{ 
  //   window.scrollTo({ 
  //     top: document.documentElement.scrollHeight, 
  //     behavior: 'auto'
  //     /* you can also use 'auto' behaviour 
  //        in place of 'smooth' */
  //   }); 
  // }; 
    
  // window.addEventListener('scroll', toggleVisible); 
  const contactUsRef = useRef();
    return (
      <section sx={styles.banner} id="home" style={{background: 'linear-gradient(110deg, #E58C22 60%, #2ABDEC 60%)', marginTop: '8em'}}>
        <Container sx={styles.banner.container}>
          <Box sx={styles.banner.contentBox}>
            <Heading as="h1" varaint="heroPrimary">
              Top Quality Services to Explore
            </Heading>
            <Text as="p" variant="heroSecondary">
                Check out our Website to find Amazing Software Products and deals! If you need Website, Application,
                or Even having Struggles Improving Current Software ? Search no Further Here at Novigo we are here to Serve!
              </Text>
              
              <section
      ref={contactUsRef}
      id="contact-us"
      sx={{ variant: "section.contactUs", ...styles.container }}
    >
              <Button
              ref={contactUsRef}
          className="donate__btn"
          variant="secondary"
          background-color="white"
          aria-label="Get Started"
          color="black"
          onClick={() => {
            console.log('contactUsRef:', contactUsRef);
            if(contactUsRef.current) {
              contactUsRef.current.scrollToBottom({ behavior: 'smooth', block: 'start' });
            }
          }}
        >
              Contact Us</Button>
              </section>
          </Box>
        </Container>
      </section>
    );
  }
}
