import { jsx } from 'theme-ui';
import { Container, Grid } from 'theme-ui';
import SectionHeader from 'components/section-header';
import FeatureCard from 'components/feature-card.js';
import Performance from 'assets/feature/performance.svg';
import Partnership from 'assets/feature/partnership.svg';
import Subscription from 'assets/feature/subscription.svg';
import Support from 'assets/feature/support.svg';

const data = [
  {
    id: 1,
    imgSrc: Performance,
    altText: 'C# SERVER SIDE PROGRAMING',
    title: 'C# Server Side programing',
    text:
      'Server Side programming with csharp dotnet and dotnet core',
  },
  {
    id: 2,
    imgSrc: Partnership,
    altText: 'DataBase Development and Management',
    title: 'DataBase Development and Management',
    text:
      'We Specialize in working with Sql and NoSql Database and can ',
  },
  {
    id: 3,
    imgSrc: Subscription,
    altText: 'Partnership deal',
    title: 'Partnership deal',
    text:
      'Let’s just get this out of the way - there will always be a kit version of Edu flow. Paid subscriptions allow us to continue helping learners around the world.',
  },
  {
    id: 4,
    imgSrc: Support,
    altText: 'Customer Support',
    title: 'Customer Support',
    text:
      'We believe it’s important for everyone to have access to software – especially when it comes to digital learning be navigated by keyboard and screen readers.',
  },
];

export default function Feature() {
  return (
   <section sx={{ variant: 'section.feature'}}>
    <Container>
      <SectionHeader
      slogan="Quality Software"
      title="Amazing usefull Services"></SectionHeader>
      </Container>
   </section>
  );
}
