import { jsx, Container, Box, Flex } from 'theme-ui';
import React, { useState } from 'react';
import Carousel from 'react-multi-carousel';
import PriceCard from 'components/price-card';
import ButtonGroup from 'components/button-group';
import SectionHeader from 'components/section-header';
import { responsive, styles } from './styles';
import data from './data';

export default function Package() {
  const { monthly, annual } = data;
  const [state, setState] = useState({
    active: 'monthly',
    pricingPlan: monthly
  })

  const handlePricingPlan = (plan) => {
    if(plan === 'annual') {
      setState({
        active: 'annual',
        pricingPlan: annual
      })
    } else {
      const data = [
        {
          id: 1,
          title: 'Set Task Instructions',
          text:
            'Access What Services Your looking for How We can help and determine the best Approach to take',
        },
        {
          id: 2,
          title: 'Contract',
          text:
            'Determine Timeline On When your looking for services to be completely met, and assess Cost of Service',
        },
        {
          id: 3,
          title: 'Proposal',
          text:
            'Send Over our Proposal ',
        },
        {
          id: 4,
          title: 'Novigo receives funds payment',
          text:
            'At Least 10% of proposal will be paid upfront to serve as a retainer for any cloud service cost incurred upon compl',
        },
      ];
      
    }
  }

  const sliderParams = {
    additionalTransfrom: 0,
    arrows: false,
    autoPlaySpeed: 3000,
    centerMode: false,
    className: '',
    slidesToSlide: 1,
    items: 3,
    containerClass: 'carousel-container',
    customButtonGroup: <ButtonGroup />,
    dotListClass: '',
    focusOnSelect: false,
    infinite: false,
    keyBoardControl: false,
    itemClass: '',
    minimumTouchDrag: 80,
    renderButtonGroupOutside: true,
    renderDotsOutside: false,
    responsive: responsive,
    showDots: false,
    sliderClass: '',
  };

  return (
    <section id="pricing" sx={{ variant: 'section.pricing'}}>
      <Container>
        <SectionHeader 
        slogan="Pricing Plan"
        title="Choose your budget"
        />

        <Flex sx={styles.buttonGroup}>
            <Box sx={styles.buttonGroupInner}>
              <button
                className={'active'}
              >

              </button>
            </Box>
           </Flex>
      </Container>
    </section>
  );
}
